static void random_move_enemy(enemy * enm, unsigned char ** map)
{
	int i = 0;
	direction_t dir = (direction_t)(rand()%4);
	int x = enm->x;
	int y = enm->y;
	int obstacle = obstacles_detection(x, y, map1, dir, 1);
	if(enm->destroyed == 0) {
		if(obstacle == BACKGROUND || obstacle == BOMBERMAN) {
			map1[y][x] = BACKGROUND;
			switch(dir) {
			case DIR_LEFT:
				x -= 1;
				break;
			case DIR_RIGHT:
				x += 1;
				break;
			case DIR_UP:
				y -= 1;
				break;
			case DIR_DOWN:
				y += 1;
				break;
			default:;
			}
			map1[y][x] = enm->type;
			enm->x = x;
			enm->y = y;
		}
	} else {
		enm->type = BACKGROUND;
		map1[y][x] = BACKGROUND;
	}
	for (i = 0; i < 100000; i++) {
	}
}

